import { Component, OnInit } from '@angular/core';
import { PopoverController } from '@ionic/angular';

@Component({
  selector: 'app-social-media',
  templateUrl: './social-media.component.html',
  styleUrls: ['./social-media.component.scss'],
})
export class SocialMediaComponent  implements OnInit {

  urlShare = '..\assets\img\photo-1575936123452-b67c3203c357.jpeg';
  textShare = 'Check out this cool image!';


  constructor(private popoverController:PopoverController) { }

  ngOnInit() {}

  shareOnFacebook() {
    const facebookUrl = `https://www.facebook.com/sharer.php?u=${encodeURIComponent(this.urlShare)}&quote=${encodeURIComponent(this.textShare)}`;

    window.open(facebookUrl, '_blank');

    // this.popoverController.dismiss();
  }

  shareOnTwitter() {
    const twitterUrl = `https://twitter.com/intent/tweet?url=${encodeURIComponent(this.urlShare)}&text=${encodeURIComponent(this.textShare)}`;

    window.open(twitterUrl, '_blank');

    // this.popoverController.dismiss();
  }
  shareOnWhatsapp() {
    const whatsappUrl = `whatsapp://send?text=${encodeURIComponent(this.textShare + ' ' + this.urlShare)}`;

    window.open(whatsappUrl, '_blank');

    // this.popoverController.dismiss();
  }

}

<ion-toolbar>
  <ion-buttons>
      <ion-button (click)="shareOnFacebook()" >
        <ion-thumbnail slot="start">
          <img src="..\assets\img\facebook.png">
          </ion-thumbnail>
      </ion-button>
      <ion-button (click)="shareOnTwitter()" >
        <ion-thumbnail slot="start">
          <img src="..\assets\img\twitter.png">
          </ion-thumbnail>
      </ion-button>
      <ion-button  (click)="shareOnWhatsapp()">
        <ion-thumbnail slot="start">
          <img src="..\assets\img\whatsapp.png">
          </ion-thumbnail>
      </ion-button>

  </ion-buttons>
</ion-toolbar>
