import { Component, OnInit } from '@angular/core';
import { PopoverController } from '@ionic/angular';

@Component({
  selector: 'app-social-media',
  templateUrl: './social-media.component.html',
  styleUrls: ['./social-media.component.scss'],
})
export class SocialMediaComponent  implements OnInit {

  constructor(private popoverController:PopoverController) { }

  ngOnInit() {}

  shareOnFacebook() {
    const shareUrl = 'https://example.com';
    const shareText = 'Check out this cool image!';

    const facebookShareUrl = `https://www.facebook.com/sharer.php?u=${encodeURIComponent(shareUrl)}&quote=${encodeURIComponent(shareText)}`;

    window.open(facebookShareUrl, '_blank');

    this.popoverController.dismiss();
  }

  shareOnTwitter() {
    const urlShare = '..\assets\img\photo-1575936123452-b67c3203c357.jpeg';
    const textShare = 'Check out this cool image!';

    const twitterShareUrl = `https://twitter.com/intent/tweet?url=${encodeURIComponent(urlShare)}&text=${encodeURIComponent(textShare)}`;

    window.open(twitterShareUrl, '_blank');

    this.popoverController.dismiss();
  }

}
