    <ion-item (click)="shareOnTwitter()">
      <ion-thumbnail slot="start">
        <img src="..\assets\img\twitter.png">
      </ion-thumbnail>
    </ion-item>
    <ion-item (click)="shareOnWhatsapp()">
      <ion-thumbnail slot="start">
        <img src="..\assets\img\whatsapp.png">
      </ion-thumbnail>
    </ion-item>
  </ion-list>
</ion-content>
